const mongoose = require('mongoose');

const DocumentSchema = new mongoose.Schema({
  fileName: {
    type: String,
    required: true,
    trim: true
  },
  fileType: {
    type: String,
    required: true,
    enum: ['text', 'markdown', 'json']
  },
  content: {
    type: String,
    required: true
  },
  size: {
    type: Number,
    required: true
  },
  hash: {
    type: String,
    required: true
  },
  uploadedBy: {
    type: String,
    default: 'system'
  },
  uploadedAt: {
    type: Date,
    default: Date.now
  },
  metadata: {
    type: Map,
    of: String
  }
}, {
  timestamps: true,
  collection: 'documents'
});

DocumentSchema.index({ fileName: 1 });
DocumentSchema.index({ uploadedAt: -1 });
DocumentSchema.index({ hash: 1 });

module.exports = mongoose.model('Document', DocumentSchema);

const mongoose = require('mongoose');

const TPMKeySchema = new mongoose.Schema({
  name: {
    type: String,
    required: true,
    unique: true,
    trim: true
  },
  keyType: {
    type: String,
    default: 'ES256',
    enum: ['ES256']
  },
  tpmHandle: {
    type: String,
    required: true,
    unique: true
  },
  publicKey: {
    type: String,
    required: true
  },
  certificateRequest: {
    type: String,
    default: null
  },
  certificate: {
    type: String,
    default: null
  },
  createdBy: {
    type: String,
    default: 'system'
  },
  createdAt: {
    type: Date,
    default: Date.now
  },
  lastUsed: {
    type: Date,
    default: null
  },
  usageCount: {
    type: Number,
    default: 0
  },
  status: {
    type: String,
    enum: ['active', 'disabled', 'deleted'],
    default: 'active'
  },
  metadata: {
    type: Map,
    of: String
  }
}, {
  timestamps: true,
  collection: 'tpmkeys'
});

TPMKeySchema.index({ name: 1 });
TPMKeySchema.index({ tpmHandle: 1 });
TPMKeySchema.index({ status: 1 });
TPMKeySchema.index({ createdAt: -1 });

module.exports = mongoose.model('TPMKey', TPMKeySchema);

const mongoose = require('mongoose');

const SignatureSchema = new mongoose.Schema({
  documentId: {
    type: mongoose.Schema.Types.ObjectId,
    ref: 'Document',
    required: true
  },
  keyId: {
    type: mongoose.Schema.Types.ObjectId,
    ref: 'TPMKey',
    required: true
  },
  signature: {
    type: String,
    required: true
  },
  algorithm: {
    type: String,
    default: 'ES256'
  },
  documentHash: {
    type: String,
    required: true
  },
  signedAt: {
    type: Date,
    default: Date.now
  },
  signedBy: {
    type: String,
    default: 'system'
  },
  verificationStatus: {
    type: String,
    enum: ['valid', 'invalid', 'pending', 'expired'],
    default: 'pending'
  },
  lastVerified: {
    type: Date,
    default: null
  },
  verificationCount: {
    type: Number,
    default: 0
  },
  metadata: {
    type: Map,
    of: String
  }
}, {
  timestamps: true,
  collection: 'signatures'
});

SignatureSchema.index({ documentId: 1 });
SignatureSchema.index({ keyId: 1 });
SignatureSchema.index({ signedAt: -1 });
SignatureSchema.index({ verificationStatus: 1 });

module.exports = mongoose.model('Signature', SignatureSchema);

const crypto = require('crypto');
const { exec } = require('child_process');
const { promisify } = require('util');
const execAsync = promisify(exec);
const logger = require('@zandd/app-logger');
const elliptic = require('elliptic');
const EC = elliptic.ec;
const ec = new EC('p256');

class TPMService {
  constructor() {
    this.isWindows = process.platform === 'win32';
    this.tpmAvailable = false;
    this.checkTPMAvailability();
  }

  async checkTPMAvailability() {
    try {
      if (this.isWindows) {
        const { stdout } = await execAsync('powershell -Command "Get-TPM | Select-Object -ExpandProperty TpmPresent"');
        this.tpmAvailable = stdout.trim().toLowerCase() === 'true';
      } else {
        const { stdout } = await execAsync('tpm2_getcap properties-fixed 2>/dev/null | grep TPM2_PT_MANUFACTURER');
        this.tpmAvailable = stdout.length > 0;
      }
      logger.info(`TPM availability: ${this.tpmAvailable}`);
    } catch (error) {
      logger.error('Error checking TPM availability:', error);
      this.tpmAvailable = false;
    }
  }

  async createES256KeyPair(keyName) {
    try {
      if (!this.tpmAvailable) {
        return this.createSoftwareES256KeyPair(keyName);
      }

      if (this.isWindows) {
        return await this.createWindowsTPMKey(keyName);
      } else {
        return await this.createLinuxTPMKey(keyName);
      }
    } catch (error) {
      logger.error('Error creating ES256 key pair:', error);
      throw error;
    }
  }

  async createSoftwareES256KeyPair(keyName) {
    try {
      const keyPair = ec.genKeyPair();
      const publicKey = keyPair.getPublic('hex');
      const privateKey = keyPair.getPrivate('hex');

      const handle = crypto.randomBytes(16).toString('hex');

      return {
        name: keyName,
        handle: handle,
        publicKey: publicKey,
        privateKey: privateKey,
        inTPM: false
      };
    } catch (error) {
      logger.error('Error creating software ES256 key pair:', error);
      throw error;
    }
  }

  async createWindowsTPMKey(keyName) {
    try {
      const script = `
        $keyName = "${keyName}"
        $key = New-Item -Path "Cert:\\CurrentUser\\My" -KeyAlgorithm ECDSA_P256 -KeyUsage DigitalSignature -Subject "CN=$keyName" -Provider "Microsoft Platform Crypto Provider"
        $thumbprint = $key.Thumbprint
        $publicKey = $key.PublicKey.EncodedKeyValue.RawData
        @{
          Handle = $thumbprint
          PublicKey = [System.Convert]::ToBase64String($publicKey)
        } | ConvertTo-Json
      `;

      const { stdout } = await execAsync(`powershell -Command "${script}"`);
      const result = JSON.parse(stdout);

      return {
        name: keyName,
        handle: result.Handle,
        publicKey: result.PublicKey,
        inTPM: true
      };
    } catch (error) {
      logger.error('Error creating Windows TPM key:', error);
      throw error;
    }
  }

  async createLinuxTPMKey(keyName) {
    try {
      const handle = `0x8100${Math.floor(Math.random() * 0xFFFF).toString(16).padStart(4, '0')}`;

      await execAsync(`tpm2_createprimary -C o -g sha256 -G ecc256 -c primary.ctx`);
      await execAsync(`tpm2_create -C primary.ctx -g sha256 -G ecc256 -u ${keyName}.pub -r ${keyName}.priv`);
      await execAsync(`tpm2_load -C primary.ctx -u ${keyName}.pub -r ${keyName}.priv -c ${keyName}.ctx`);
      await execAsync(`tpm2_evictcontrol -C o -c ${keyName}.ctx ${handle}`);

      const { stdout } = await execAsync(`tpm2_readpublic -c ${handle} -f pem`);

      return {
        name: keyName,
        handle: handle,
        publicKey: stdout,
        inTPM: true
      };
    } catch (error) {
      logger.error('Error creating Linux TPM key:', error);
      throw error;
    }
  }

  async signDocument(documentHash, keyHandle, isTPMKey = true) {
    try {
      if (!isTPMKey) {
        return this.signWithSoftwareKey(documentHash, keyHandle);
      }

      if (this.isWindows) {
        return await this.signWithWindowsTPM(documentHash, keyHandle);
      } else {
        return await this.signWithLinuxTPM(documentHash, keyHandle);
      }
    } catch (error) {
      logger.error('Error signing document:', error);
      throw error;
    }
  }

  signWithSoftwareKey(documentHash, privateKey) {
    try {
      const key = ec.keyFromPrivate(privateKey, 'hex');
      const signature = key.sign(documentHash);
      return signature.toDER('hex');
    } catch (error) {
      logger.error('Error signing with software key:', error);
      throw error;
    }
  }

  async signWithWindowsTPM(documentHash, keyHandle) {
    try {
      const script = `
        $thumbprint = "${keyHandle}"
        $cert = Get-ChildItem -Path Cert:\\CurrentUser\\My | Where-Object {$_.Thumbprint -eq $thumbprint}
        $data = [System.Convert]::FromBase64String("${Buffer.from(documentHash, 'hex').toString('base64')}")
        $signature = $cert.PrivateKey.SignData($data, [System.Security.Cryptography.HashAlgorithmName]::SHA256, [System.Security.Cryptography.RSASignaturePadding]::Pkcs1)
        [System.Convert]::ToBase64String($signature)
      `;

      const { stdout } = await execAsync(`powershell -Command "${script}"`);
      return Buffer.from(stdout.trim(), 'base64').toString('hex');
    } catch (error) {
      logger.error('Error signing with Windows TPM:', error);
      throw error;
    }
  }

  async signWithLinuxTPM(documentHash, keyHandle) {
    try {
      const hashFile = `/tmp/hash_${Date.now()}.bin`;
      const sigFile = `/tmp/sig_${Date.now()}.bin`;

      await execAsync(`echo -n "${documentHash}" | xxd -r -p > ${hashFile}`);
      await execAsync(`tpm2_sign -c ${keyHandle} -g sha256 -s rsassa -o ${sigFile} ${hashFile}`);
      const { stdout } = await execAsync(`xxd -p -c 256 ${sigFile}`);

      await execAsync(`rm -f ${hashFile} ${sigFile}`);

      return stdout.trim();
    } catch (error) {
      logger.error('Error signing with Linux TPM:', error);
      throw error;
    }
  }

  async verifySignature(documentHash, signature, publicKey) {
    try {
      const key = ec.keyFromPublic(publicKey, 'hex');
      return key.verify(documentHash, signature);
    } catch (error) {
      logger.error('Error verifying signature:', error);
      return false;
    }
  }

  async deleteKey(keyHandle, isTPMKey = true) {
    try {
      if (!isTPMKey) {
        return true;
      }

      if (this.isWindows) {
        return await this.deleteWindowsTPMKey(keyHandle);
      } else {
        return await this.deleteLinuxTPMKey(keyHandle);
      }
    } catch (error) {
      logger.error('Error deleting key:', error);
      throw error;
    }
  }

  async deleteWindowsTPMKey(keyHandle) {
    try {
      const script = `
        $thumbprint = "${keyHandle}"
        Remove-Item -Path "Cert:\\CurrentUser\\My\\$thumbprint" -Force
      `;

      await execAsync(`powershell -Command "${script}"`);
      return true;
    } catch (error) {
      logger.error('Error deleting Windows TPM key:', error);
      throw error;
    }
  }

  async deleteLinuxTPMKey(keyHandle) {
    try {
      await execAsync(`tpm2_evictcontrol -C o -c ${keyHandle}`);
      return true;
    } catch (error) {
      logger.error('Error deleting Linux TPM key:', error);
      throw error;
    }
  }

  async generateCSR(keyName, keyHandle, publicKey, commonName, organization, country) {
    try {
      const forge = require('node-forge');
      const csr = forge.pki.createCertificationRequest();

      csr.publicKey = forge.pki.publicKeyFromPem(this.convertToPEM(publicKey));
      csr.setSubject([
        { name: 'commonName', value: commonName || keyName },
        { name: 'organizationName', value: organization || 'TPM20 Organization' },
        { name: 'countryName', value: country || 'US' }
      ]);

      csr.setAttributes([
        {
          name: 'extensionRequest',
          extensions: [
            {
              name: 'keyUsage',
              keyCertSign: false,
              digitalSignature: true,
              nonRepudiation: true,
              keyEncipherment: false,
              dataEncipherment: false
            }
          ]
        }
      ]);

      const csrPem = forge.pki.certificationRequestToPem(csr);
      return csrPem;
    } catch (error) {
      logger.error('Error generating CSR:', error);
      throw error;
    }
  }

  convertToPEM(publicKey) {
    if (publicKey.includes('-----BEGIN')) {
      return publicKey;
    }

    return `-----BEGIN PUBLIC KEY-----\n${publicKey}\n-----END PUBLIC KEY-----`;
  }

  calculateHash(content) {
    return crypto.createHash('sha256').update(content).digest('hex');
  }
}

module.exports = new TPMService();

const logger = require('@zandd/app-logger');
const TPMKey = require('../models/TPMKey');
const tpmService = require('../services/tpmService');

class KeyManagementController {
  async listKeys(req, res) {
    try {
      const keys = await TPMKey.find({ status: { $ne: 'deleted' } })
        .sort({ createdAt: -1 })
        .lean();

      res.render('keys', {
        title: 'TPM Key Management',
        keys: keys,
        success: req.query.success,
        error: req.query.error
      });
    } catch (error) {
      logger.error('Error listing keys:', error);
      res.status(500).render('errorPage', {
        title: 'Error',
        error: 'Failed to retrieve keys'
      });
    }
  }

  async createKey(req, res) {
    try {
      const { keyName, description } = req.body;

      if (!keyName) {
        return res.status(400).json({ error: 'Key name is required' });
      }

      const existingKey = await TPMKey.findOne({ name: keyName });
      if (existingKey) {
        return res.status(400).json({ error: 'Key name already exists' });
      }

      const keyData = await tpmService.createES256KeyPair(keyName);

      const newKey = new TPMKey({
        name: keyName,
        tpmHandle: keyData.handle,
        publicKey: keyData.publicKey,
        metadata: new Map([
          ['description', description || ''],
          ['inTPM', keyData.inTPM ? 'true' : 'false']
        ])
      });

      await newKey.save();
      logger.info(`Created new TPM key: ${keyName}`);

      res.json({
        success: true,
        key: {
          id: newKey._id,
          name: newKey.name,
          publicKey: newKey.publicKey,
          createdAt: newKey.createdAt
        }
      });
    } catch (error) {
      logger.error('Error creating key:', error);
      res.status(500).json({ error: 'Failed to create key' });
    }
  }

  async deleteKey(req, res) {
    try {
      const { keyId } = req.params;

      const key = await TPMKey.findById(keyId);
      if (!key) {
        return res.status(404).json({ error: 'Key not found' });
      }

      const isTPMKey = key.metadata && key.metadata.get('inTPM') === 'true';
      await tpmService.deleteKey(key.tpmHandle, isTPMKey);

      key.status = 'deleted';
      await key.save();

      logger.info(`Deleted TPM key: ${key.name}`);

      res.json({ success: true, message: 'Key deleted successfully' });
    } catch (error) {
      logger.error('Error deleting key:', error);
      res.status(500).json({ error: 'Failed to delete key' });
    }
  }

  async viewKey(req, res) {
    try {
      const { keyId } = req.params;

      const key = await TPMKey.findById(keyId).lean();
      if (!key) {
        return res.status(404).render('errorPage', {
          title: 'Error',
          error: 'Key not found'
        });
      }

      res.render('keyDetail', {
        title: `Key: ${key.name}`,
        key: key
      });
    } catch (error) {
      logger.error('Error viewing key:', error);
      res.status(500).render('errorPage', {
        title: 'Error',
        error: 'Failed to retrieve key details'
      });
    }
  }

  async generateCSR(req, res) {
    try {
      const { keyId } = req.params;
      const { commonName, organization, country } = req.body;

      const key = await TPMKey.findById(keyId);
      if (!key) {
        return res.status(404).json({ error: 'Key not found' });
      }

      const csr = await tpmService.generateCSR(
        key.name,
        key.tpmHandle,
        key.publicKey,
        commonName,
        organization,
        country
      );

      key.certificateRequest = csr;
      await key.save();

      logger.info(`Generated CSR for key: ${key.name}`);

      res.json({
        success: true,
        csr: csr
      });
    } catch (error) {
      logger.error('Error generating CSR:', error);
      res.status(500).json({ error: 'Failed to generate CSR' });
    }
  }

  async uploadCertificate(req, res) {
    try {
      const { keyId } = req.params;
      const { certificate } = req.body;

      const key = await TPMKey.findById(keyId);
      if (!key) {
        return res.status(404).json({ error: 'Key not found' });
      }

      key.certificate = certificate;
      await key.save();

      logger.info(`Uploaded certificate for key: ${key.name}`);

      res.json({
        success: true,
        message: 'Certificate uploaded successfully'
      });
    } catch (error) {
      logger.error('Error uploading certificate:', error);
      res.status(500).json({ error: 'Failed to upload certificate' });
    }
  }
}

module.exports = new KeyManagementController();

const logger = require('@zandd/app-logger');
const Document = require('../models/Document');
const TPMKey = require('../models/TPMKey');
const Signature = require('../models/Signature');
const tpmService = require('../services/tpmService');
const multer = require('multer');
const path = require('path');
const fs = require('fs').promises;

const upload = multer({
  storage: multer.memoryStorage(),
  limits: {
    fileSize: 10 * 1024 * 1024
  },
  fileFilter: (req, file, cb) => {
    const allowedTypes = ['.txt', '.md', '.json'];
    const ext = path.extname(file.originalname).toLowerCase();
    if (allowedTypes.includes(ext)) {
      cb(null, true);
    } else {
      cb(new Error('Invalid file type. Only .txt, .md, and .json files are allowed.'));
    }
  }
}).single('document');

class DocumentController {
  async listDocuments(req, res) {
    try {
      const documents = await Document.find()
        .sort({ uploadedAt: -1 })
        .lean();

      res.render('documents', {
        title: 'Document Management',
        documents: documents,
        success: req.query.success,
        error: req.query.error
      });
    } catch (error) {
      logger.error('Error listing documents:', error);
      res.status(500).render('errorPage', {
        title: 'Error',
        error: 'Failed to retrieve documents'
      });
    }
  }

  async uploadDocument(req, res) {
    upload(req, res, async (err) => {
      if (err) {
        logger.error('Upload error:', err);
        return res.status(400).json({ error: err.message });
      }

      try {
        if (!req.file) {
          return res.status(400).json({ error: 'No file uploaded' });
        }

        const content = req.file.buffer.toString('utf-8');
        const hash = tpmService.calculateHash(content);
        const ext = path.extname(req.file.originalname).toLowerCase();

        let fileType = 'text';
        if (ext === '.md') fileType = 'markdown';
        else if (ext === '.json') fileType = 'json';

        const existingDoc = await Document.findOne({ hash: hash });
        if (existingDoc) {
          return res.status(400).json({ error: 'Document with same content already exists' });
        }

        const newDocument = new Document({
          fileName: req.file.originalname,
          fileType: fileType,
          content: content,
          size: req.file.size,
          hash: hash,
          uploadedBy: req.body.uploadedBy || 'user'
        });

        await newDocument.save();
        logger.info(`Document uploaded: ${req.file.originalname}`);

        res.json({
          success: true,
          document: {
            id: newDocument._id,
            fileName: newDocument.fileName,
            fileType: newDocument.fileType,
            size: newDocument.size,
            hash: newDocument.hash,
            uploadedAt: newDocument.uploadedAt
          }
        });
      } catch (error) {
        logger.error('Error saving document:', error);
        res.status(500).json({ error: 'Failed to save document' });
      }
    });
  }

  async viewDocument(req, res) {
    try {
      const { documentId } = req.params;

      const document = await Document.findById(documentId).lean();
      if (!document) {
        return res.status(404).render('errorPage', {
          title: 'Error',
          error: 'Document not found'
        });
      }

      const signatures = await Signature.find({ documentId: documentId })
        .populate('keyId', 'name')
        .sort({ signedAt: -1 })
        .lean();

      res.render('documentDetail', {
        title: `Document: ${document.fileName}`,
        document: document,
        signatures: signatures
      });
    } catch (error) {
      logger.error('Error viewing document:', error);
      res.status(500).render('errorPage', {
        title: 'Error',
        error: 'Failed to retrieve document details'
      });
    }
  }

  async signDocument(req, res) {
    try {
      const { documentId } = req.params;
      const { keyId } = req.body;

      if (!keyId) {
        return res.status(400).json({ error: 'Key ID is required' });
      }

      const document = await Document.findById(documentId);
      if (!document) {
        return res.status(404).json({ error: 'Document not found' });
      }

      const key = await TPMKey.findById(keyId);
      if (!key || key.status !== 'active') {
        return res.status(404).json({ error: 'Active key not found' });
      }

      const documentHash = document.hash;
      const isTPMKey = key.metadata && key.metadata.get('inTPM') === 'true';
      const signature = await tpmService.signDocument(documentHash, key.tpmHandle, isTPMKey);

      const newSignature = new Signature({
        documentId: documentId,
        keyId: keyId,
        signature: signature,
        documentHash: documentHash,
        signedBy: req.body.signedBy || 'user'
      });

      await newSignature.save();

      key.lastUsed = new Date();
      key.usageCount = (key.usageCount || 0) + 1;
      await key.save();

      logger.info(`Document signed: ${document.fileName} with key: ${key.name}`);

      res.json({
        success: true,
        signature: {
          id: newSignature._id,
          signature: signature,
          signedAt: newSignature.signedAt
        }
      });
    } catch (error) {
      logger.error('Error signing document:', error);
      res.status(500).json({ error: 'Failed to sign document' });
    }
  }

  async verifySignature(req, res) {
    try {
      const { signatureId } = req.params;

      const signature = await Signature.findById(signatureId)
        .populate('documentId')
        .populate('keyId');

      if (!signature) {
        return res.status(404).json({ error: 'Signature not found' });
      }

      const document = signature.documentId;
      const key = signature.keyId;

      const currentHash = tpmService.calculateHash(document.content);

      let isValid = false;
      if (currentHash === signature.documentHash) {
        isValid = await tpmService.verifySignature(
          signature.documentHash,
          signature.signature,
          key.publicKey
        );
      }

      signature.verificationStatus = isValid ? 'valid' : 'invalid';
      signature.lastVerified = new Date();
      signature.verificationCount = (signature.verificationCount || 0) + 1;
      await signature.save();

      logger.info(`Signature verified: ${signature._id} - ${isValid ? 'Valid' : 'Invalid'}`);

      res.json({
        success: true,
        valid: isValid,
        message: isValid ? 
          'Signature is valid' : 
          'Signature is invalid or document has been modified'
      });
    } catch (error) {
      logger.error('Error verifying signature:', error);
      res.status(500).json({ error: 'Failed to verify signature' });
    }
  }

  async deleteDocument(req, res) {
    try {
      const { documentId } = req.params;

      const signatures = await Signature.find({ documentId: documentId });
      if (signatures.length > 0) {
        return res.status(400).json({ 
          error: 'Cannot delete document with existing signatures' 
        });
      }

      const document = await Document.findByIdAndDelete(documentId);
      if (!document) {
        return res.status(404).json({ error: 'Document not found' });
      }

      logger.info(`Document deleted: ${document.fileName}`);

      res.json({
        success: true,
        message: 'Document deleted successfully'
      });
    } catch (error) {
      logger.error('Error deleting document:', error);
      res.status(500).json({ error: 'Failed to delete document' });
    }
  }
}

module.exports = new DocumentController();

