const { expect } = require('chai');
const sinon = require('sinon');
const tpmService = require('../services/tpmService');

describe('TPM Service', () => {
  let sandbox;

  beforeEach(() => {
    sandbox = sinon.createSandbox();
  });

  afterEach(() => {
    sandbox.restore();
  });

  describe('checkTPMAvailability', () => {
    it('should detect TPM availability on Windows', async () => {
      const originalPlatform = Object.getOwnPropertyDescriptor(process, 'platform');
      Object.defineProperty(process, 'platform', {
        value: 'win32'
      });

      await tpmService.checkTPMAvailability();

      if (originalPlatform) {
        Object.defineProperty(process, 'platform', originalPlatform);
      }
    });

    it('should detect TPM availability on Linux', async () => {
      const originalPlatform = Object.getOwnPropertyDescriptor(process, 'platform');
      Object.defineProperty(process, 'platform', {
        value: 'linux'
      });

      await tpmService.checkTPMAvailability();

      if (originalPlatform) {
        Object.defineProperty(process, 'platform', originalPlatform);
      }
    });
  });

  describe('createES256KeyPair', () => {
    it('should create a software key pair when TPM is not available', async () => {
      tpmService.tpmAvailable = false;

      const result = await tpmService.createES256KeyPair('testKey');

      expect(result).to.have.property('name', 'testKey');
      expect(result).to.have.property('handle');
      expect(result).to.have.property('publicKey');
      expect(result).to.have.property('inTPM', false);
    });

    it('should handle key creation errors', async () => {
      tpmService.tpmAvailable = false;
      sandbox.stub(tpmService, 'createSoftwareES256KeyPair').throws(new Error('Key creation failed'));

      try {
        await tpmService.createES256KeyPair('testKey');
        expect.fail('Should have thrown an error');
      } catch (error) {
        expect(error.message).to.equal('Key creation failed');
      }
    });
  });

  describe('calculateHash', () => {
    it('should calculate SHA256 hash of content', () => {
      const content = 'Hello, World!';
      const hash = tpmService.calculateHash(content);

      expect(hash).to.be.a('string');
      expect(hash).to.have.lengthOf(64);
      expect(hash).to.equal('dffd6021bb2bd5b0af676290809ec3a53191dd81c7f70a4b28688a362182986f');
    });

    it('should produce different hashes for different content', () => {
      const hash1 = tpmService.calculateHash('content1');
      const hash2 = tpmService.calculateHash('content2');

      expect(hash1).to.not.equal(hash2);
    });
  });

  describe('convertToPEM', () => {
    it('should return PEM if already in PEM format', () => {
      const pemKey = '-----BEGIN PUBLIC KEY-----\nMIIBIjANBgkqhkiG9w0BAQEFAAOCAQ8AMIIBCgKCAQEA\n-----END PUBLIC KEY-----';
      const result = tpmService.convertToPEM(pemKey);

      expect(result).to.equal(pemKey);
    });

    it('should convert to PEM format if not already', () => {
      const rawKey = 'MIIBIjANBgkqhkiG9w0BAQEFAAOCAQ8AMIIBCgKCAQEA';
      const result = tpmService.convertToPEM(rawKey);

      expect(result).to.include('-----BEGIN PUBLIC KEY-----');
      expect(result).to.include('-----END PUBLIC KEY-----');
      expect(result).to.include(rawKey);
    });
  });

  describe('signWithSoftwareKey', () => {
    it('should sign data with software key', () => {
      const documentHash = tpmService.calculateHash('test document');
      const keyPair = tpmService.ec.genKeyPair();
      const privateKey = keyPair.getPrivate('hex');

      const signature = tpmService.signWithSoftwareKey(documentHash, privateKey);

      expect(signature).to.be.a('string');
      expect(signature.length).to.be.greaterThan(0);
    });
  });

  describe('verifySignature', () => {
    it('should verify valid signature', async () => {
      const documentHash = tpmService.calculateHash('test document');
      const keyPair = tpmService.ec.genKeyPair();
      const privateKey = keyPair.getPrivate('hex');
      const publicKey = keyPair.getPublic('hex');

      const signature = tpmService.signWithSoftwareKey(documentHash, privateKey);
      const isValid = await tpmService.verifySignature(documentHash, signature, publicKey);

      expect(isValid).to.be.true;
    });

    it('should reject invalid signature', async () => {
      const documentHash = tpmService.calculateHash('test document');
      const keyPair1 = tpmService.ec.genKeyPair();
      const keyPair2 = tpmService.ec.genKeyPair();
      const privateKey1 = keyPair1.getPrivate('hex');
      const publicKey2 = keyPair2.getPublic('hex');

      const signature = tpmService.signWithSoftwareKey(documentHash, privateKey1);
      const isValid = await tpmService.verifySignature(documentHash, signature, publicKey2);

      expect(isValid).to.be.false;
    });
  });
});

const { expect } = require('chai');
const mongoose = require('mongoose');
const Document = require('../models/Document');
const TPMKey = require('../models/TPMKey');
const Signature = require('../models/Signature');

describe('MongoDB Models', () => {
  before(async () => {
    await mongoose.connect('mongodb://192.168.129.197:27017/tpm20_test', {
      useNewUrlParser: true,
      useUnifiedTopology: true
    });
  });

  after(async () => {
    await mongoose.connection.close();
  });

  beforeEach(async () => {
    await Document.deleteMany({});
    await TPMKey.deleteMany({});
    await Signature.deleteMany({});
  });

  describe('Document Model', () => {
    it('should create a valid document', async () => {
      const doc = new Document({
        fileName: 'test.txt',
        fileType: 'text',
        content: 'Test content',
        size: 12,
        hash: 'abc123'
      });

      const saved = await doc.save();

      expect(saved.fileName).to.equal('test.txt');
      expect(saved.fileType).to.equal('text');
      expect(saved.content).to.equal('Test content');
      expect(saved.size).to.equal(12);
      expect(saved.hash).to.equal('abc123');
    });

    it('should require required fields', async () => {
      const doc = new Document({
        fileName: 'test.txt'
      });

      try {
        await doc.save();
        expect.fail('Should have thrown validation error');
      } catch (error) {
        expect(error).to.be.instanceOf(mongoose.Error.ValidationError);
      }
    });

    it('should validate file type enum', async () => {
      const doc = new Document({
        fileName: 'test.txt',
        fileType: 'invalid',
        content: 'Test',
        size: 4,
        hash: 'abc'
      });

      try {
        await doc.save();
        expect.fail('Should have thrown validation error');
      } catch (error) {
        expect(error).to.be.instanceOf(mongoose.Error.ValidationError);
      }
    });
  });

  describe('TPMKey Model', () => {
    it('should create a valid TPM key', async () => {
      const key = new TPMKey({
        name: 'TestKey',
        tpmHandle: '0x81000001',
        publicKey: 'public-key-data'
      });

      const saved = await key.save();

      expect(saved.name).to.equal('TestKey');
      expect(saved.tpmHandle).to.equal('0x81000001');
      expect(saved.publicKey).to.equal('public-key-data');
      expect(saved.keyType).to.equal('ES256');
      expect(saved.status).to.equal('active');
      expect(saved.usageCount).to.equal(0);
    });

    it('should enforce unique key names', async () => {
      const key1 = new TPMKey({
        name: 'UniqueKey',
        tpmHandle: '0x81000001',
        publicKey: 'key1'
      });
      await key1.save();

      const key2 = new TPMKey({
        name: 'UniqueKey',
        tpmHandle: '0x81000002',
        publicKey: 'key2'
      });

      try {
        await key2.save();
        expect.fail('Should have thrown duplicate key error');
      } catch (error) {
        expect(error.code).to.equal(11000);
      }
    });

    it('should validate status enum', async () => {
      const key = new TPMKey({
        name: 'TestKey',
        tpmHandle: '0x81000001',
        publicKey: 'public-key',
        status: 'invalid'
      });

      try {
        await key.save();
        expect.fail('Should have thrown validation error');
      } catch (error) {
        expect(error).to.be.instanceOf(mongoose.Error.ValidationError);
      }
    });
  });

  describe('Signature Model', () => {
    it('should create a valid signature', async () => {
      const doc = await new Document({
        fileName: 'test.txt',
        fileType: 'text',
        content: 'Test',
        size: 4,
        hash: 'abc123'
      }).save();

      const key = await new TPMKey({
        name: 'SignKey',
        tpmHandle: '0x81000001',
        publicKey: 'public-key'
      }).save();

      const sig = new Signature({
        documentId: doc._id,
        keyId: key._id,
        signature: 'signature-data',
        documentHash: 'abc123'
      });

      const saved = await sig.save();

      expect(saved.documentId.toString()).to.equal(doc._id.toString());
      expect(saved.keyId.toString()).to.equal(key._id.toString());
      expect(saved.signature).to.equal('signature-data');
      expect(saved.algorithm).to.equal('ES256');
      expect(saved.verificationStatus).to.equal('pending');
      expect(saved.verificationCount).to.equal(0);
    });

    it('should require document and key references', async () => {
      const sig = new Signature({
        signature: 'signature-data',
        documentHash: 'abc123'
      });

      try {
        await sig.save();
        expect.fail('Should have thrown validation error');
      } catch (error) {
        expect(error).to.be.instanceOf(mongoose.Error.ValidationError);
      }
    });

    it('should validate verification status enum', async () => {
      const doc = await new Document({
        fileName: 'test.txt',
        fileType: 'text',
        content: 'Test',
        size: 4,
        hash: 'abc123'
      }).save();

      const key = await new TPMKey({
        name: 'SignKey',
        tpmHandle: '0x81000001',
        publicKey: 'public-key'
      }).save();

      const sig = new Signature({
        documentId: doc._id,
        keyId: key._id,
        signature: 'signature-data',
        documentHash: 'abc123',
        verificationStatus: 'invalid-status'
      });

      try {
        await sig.save();
        expect.fail('Should have thrown validation error');
      } catch (error) {
        expect(error).to.be.instanceOf(mongoose.Error.ValidationError);
      }
    });
  });
});

